			end
		else
			Output("[ Delete Parent ]","",function()
				DismissAll()
				wait()
				Output("Are you sure?","",nil)
				Output("Yes","",function() DismissAll() ExploreThis:Destroy()
					wait()
					DismissOutput()
					Output("[ Explorer ]","",function()
