	function KD(key) 
		if key == "0" then 
			Shift = true 
			if pose.Value == 1 then 
				pose.Value = 0 
				disableJump = false 
			end 
		elseif key == "4" then 
			Alt = true 
			if not Animating and SlideCooldown == 0 then 
				invoke.Value = 3 
			else 
				while Animating or SlideCooldown > 0 do 
					wait() 
				end 
				if Alt then 
					invoke.Value = 3 
				end 
			end 
		elseif key == "2" then 
			if not ThrowingSequence then 
				if pose.Value == 0 then 
					pose.Value = 1 
					disableJump = true 
				elseif pose.Value == 1 then 
					pose.Value = 0 
					disableJump = false 
				end 
			end 
		elseif key == string.char(32) then 
			Space = true 
			if pose.Value == 1 then 
				if not Animating then 
					invoke.Value = 2 
				else 
					while Animating do 
						wait() 
					end 
					if Space and pose.Value == 1 then 
						invoke.Value = 2 
					end 
				end 
			end 
		end 
	end 

	function KU(key) 
		if key == "0" then 
			Shift = false 
		elseif key == "4" then 
			Alt = false 
		elseif key == string.char(32) then 
			Space = false 
		end 
	end 

