			ray.Friction = 0 
			ray.Transparency = 0 

			local finding = true 
			far = 1000 

			while finding do 
				far = far - 1 
				if far > 0 then 
					local see = ray.Position + v 
					ray.Position = ray.Position + v 
					if ray.Position ~= see then 
						finding = false 
						found = see 
