							Chat.FontSize = "Size12" 
							Chat.Position = UDim2.new(0.599999964, 0, 0, 0) 
							Chat.Visible = true 
							Chat.Text = "Critical!" 
							coroutine.resume(coroutine.create(function(Part) 
								for i=0,4,0.1 do
									wait()
									Part.CFrame=Part.CFrame*cf(0,0.05,0)
								end
								Part.Parent=nil
							end),p) 
							for i=0,7 do
								Blood=it("Part")
