		Selected = true
		mouse.KeyDown:connect(function(key)
			key = key:lower()
			if Reloading == false and Slashing == false and Shooting == false then
				if key == "f" then
					Reload()
				end
			end
			if Shooting == false then
				if key == "e" then
					Keys.e = true
					local k
					Spec.BorderColor3 = C3(1, 1, 0.4)
					repeat
						wait()
						k = mouse.KeyUp:wait()
					until k == "e"
					Keys.e = false
					if Shooting == false then
						Spec.BorderColor3 = C3()
					end
				end
			end
		end)
		mouse.Button1Down:connect(function()
			if Reloading == false and Slashing == false and Shooting == false then
				if ArrowOn == false then
					local yesh = true
					Notime(function()
						mouse.Button1Up:wait()
						yesh = false
					end)
					local ah = Keys.e
					Reload()
					if yesh then
						local mm = Special >= 50
						if ah and mm or Keys.e and mm then
							SpecialAtk(mouse)
						else
							Shoot(mouse)
						end
					end
				else
					local mm = Special >= 50
					if Keys.e and mm then
						SpecialAtk(mouse)
					else
						Shoot(mouse)
					end
				end
			end
		end)
