		local FXTable = {}

		function Bezier(startpos, pos2, pos3, endpos, t)
			local A = startpos:lerp(pos2, t)
			local B  = pos2:lerp(pos3, t)
			local C = pos3:lerp(endpos, t)
			local lerp1 = A:lerp(B, t)
			local lerp2 = B:lerp(C, t)
			local cubic = lerp1:lerp(lerp2, t)
			return cubic
		end

		function Tween(obj,props,time,easing,direction,repeats,backwards)
			local info = TweenInfo.new(time or .5, easing or Enum.EasingStyle.Quad, direction or Enum.EasingDirection.Out, repeats or 0, backwards or false)
			local tween = S.TweenService:Create(obj, info, props)

			tween:Play()
		end

		coroutine.resume(coroutine.create(function()
			while true do
				for i = 1, #FXTable do
					local data = FXTable[i]
					if(data)then
						local Frame = data.Frame
						local FX = data.Effect or 'ResizeAndFade'
						local Parent = data.Parent or Effects
						local Color = data.Color or C3.N(0,0,0)
						local Size = data.Size or V3.N(1,1,1)
						local MoveDir = data.MoveDirection or nil
						local MeshData = data.Mesh or nil
						local SndData = data.Sound or nil
						local Frames = data.Frames or 45
						local CFra = data.CFrame or Torso.CFrame
						local Settings = data.FXSettings or {}
						local Prt,Msh,Snd = data.Part,data.Mesh,data.Sound
						local grow = data.Grow

						local MoveSpeed = nil;
						if(MoveDir)then
							MoveSpeed = (CFra.p - MoveDir).magnitude/Frames
						end
						if(FX ~= 'Arc')then
							Frame = Frame + 1
							if(FX == "Fade")then
								Prt.Transparency  = (Frame/Frames)
							elseif(FX == "Resize")then
								if(not Settings.EndSize)then
									Settings.EndSize = V3.N(0,0,0)
								end
								if(Settings.EndIsIncrement)then
									if(Msh)then
										Msh.Scale = Msh.Scale + Settings.EndSize
									else
										Prt.Size = Prt.Size + Settings.EndSize
									end					
								else
									if(Msh)then
										Msh.Scale = Msh.Scale - grow/Frames
									else
										Prt.Size = Prt.Size - grow/Frames
									end
								end 
							elseif(FX == "ResizeAndFade")then
								if(not Settings.EndSize)then
									Settings.EndSize = V3.N(0,0,0)
								end
								if(Settings.EndIsIncrement)then
									if(Msh)then
										Msh.Scale = Msh.Scale + Settings.EndSize
									else
										Prt.Size = Prt.Size + Settings.EndSize
									end					
								else
									if(Msh)then
										Msh.Scale = Msh.Scale - grow/Frames
									else
										Prt.Size = Prt.Size - grow/Frames
									end
								end 
								Prt.Transparency = (Frame/Frames)
							end
							if(Settings.RandomizeCFrame)then
								Prt.CFrame = Prt.CFrame * CF.A(M.RRNG(-360,360),M.RRNG(-360,360),M.RRNG(-360,360))
							end
							if(MoveDir and MoveSpeed)then
								local Orientation = Prt.Orientation
								Prt.CFrame = CF.N(Prt.Position,MoveDir)*CF.N(0,0,-MoveSpeed)
								Prt.Orientation = Orientation
							end
							if(Prt.Transparency >= 1 or Frame >= Frames)then
								Prt:destroy()
								table.remove(FXTable,i)
							else
								data.Frame = Frame
							end
						else
							local start,third,fourth,endP = Settings.Start,Settings.Third,Settings.Fourth,Settings.End
							if(not Settings.End and Settings.Home)then endP = Settings.Home.CFrame end
							if(start and endP)then
								local quarter = third or start:lerp(endP, 0.25) * CF.N(M.RNG(-25,25),M.RNG(0,25),M.RNG(-25,25))
								local threequarter = fourth or start:lerp(endP, 0.75) * CF.N(M.RNG(-25,25),M.RNG(0,25),M.RNG(-25,25))
								Frame = Frame + Settings.Speed or 0.01
								if(Settings.Home)then
									endP = Settings.Home.CFrame
								end
								Prt.CFrame = Bezier(start, quarter, threequarter, endP, i)
								if(Prt.Transparency >= 1 or Frame >= Frames)then
									if(Settings.RemoveOnGoal)then
										Prt:destroy()
									end
								end
							else

							end
						end
					end
				end
				swait()
			end
		end))

		function Effect(data)
			local FX = data.Effect or 'ResizeAndFade'
			local Parent = data.Parent or Effects
			local Color = data.Color or C3.N(0,0,0)
			local Size = data.Size or V3.N(1,1,1)
			local MoveDir = data.MoveDirection or nil
			local MeshData = data.Mesh or nil
			local SndData = data.Sound or nil
			local Frames = data.Frames or 45
			local Manual = data.Manual or nil
			local Material = data.Material or nil
			local CFra = data.CFrame or Torso.CFrame
			local Settings = data.FXSettings or {}
			local Shape = data.Shape or Enum.PartType.Block
			local Snd,Prt,Msh;
			if(Manual and typeof(Manual) == 'Instance' and Manual:IsA'BasePart')then
				Prt = Manual
			else
				Prt = Part(Parent,Color,Material,Size,CFra,true,false)
				Prt.Shape = Shape
			end
			if(typeof(MeshData) == 'table')then
				Msh = Mesh(Prt,MeshData.MeshType,MeshData.MeshId,MeshData.TextureId,MeshData.Scale,MeshData.Offset)
			elseif(typeof(MeshData) == 'Instance')then
				Msh = MeshData:Clone()
				Msh.Parent = Prt
			elseif(Shape == Enum.PartType.Block)then
				Msh = Mesh(Prt,Enum.MeshType.Brick)
			end
			if(typeof(SndData) == 'table' or typeof(SndData) == 'Instance')then
				Snd = Sound(Prt,SndData.SoundId,SndData.Pitch,SndData.Volume,false,false,true)
			end
			if(Snd)then
				repeat wait() until Snd.Playing and Snd.IsLoaded and Snd.TimeLength > 0
				data.Frames = Snd.TimeLength * Frame_Speed/Snd.Pitch
			end
			data.Part = Prt
			data.Mesh = Msh
			data.Sound = Snd
			data.Frame = 0
			data.Size = (Msh and Msh.Scale or Size)
			Size = (Msh and Msh.Scale or Size)
			data.Grow = Size-(Settings.EndSize or (Msh and Msh.Scale or Size)/2)
			table.insert(FXTable,data)
			return Prt,Msh,Snd
		end

		function Zap(data)
			local sCF,eCF = data.StartCFrame,data.EndCFrame
			assert(sCF,"You need a start CFrame!")
			assert(eCF,"You need an end CFrame!")
			local parts = data.PartCount or 15
			local zapRot = data.ZapRotation or {-5,5}
			local startThick = data.StartSize or 3;
			local endThick = data.EndSize or startThick/2;
			local color = data.Color or BrickColor.new'Electric blue'
			local delay = data.Delay or 35
			local delayInc = data.DelayInc or 0
			local lastLightning;
			local MagZ = (sCF.p - eCF.p).magnitude
			local thick = startThick
			local inc = (startThick/parts)-(endThick/parts)

			for i = 1, parts do
				local pos = sCF.p
				if(lastLightning)then
					pos = lastLightning.CFrame*CF.N(0,0,MagZ/parts/2).p
				end
				delay = delay + delayInc
				local zapPart = Part(Effects,color,Enum.Material.Neon,V3.N(thick,thick,MagZ/parts),CF.N(pos),true,false)
				local posie = CF.N(pos,eCF.p)*CF.N(0,0,MagZ/parts).p+V3.N(M.RNG(unpack(zapRot)),M.RNG(unpack(zapRot)),M.RNG(unpack(zapRot)))
				if(parts == i)then
					local MagZ = (pos-eCF.p).magnitude
					zapPart.Size = V3.N(endThick,endThick,MagZ)
					zapPart.CFrame = CF.N(pos, eCF.p)*CF.N(0,0,-MagZ/2)
					Effect{Effect='ResizeAndFade',Size=V3.N(thick,thick,thick),CFrame=eCF*CF.A(M.RRNG(-180,180),M.RRNG(-180,180),M.RRNG(-180,180)),Color=color,Frames=delay*2,FXSettings={EndSize=V3.N(thick*8,thick*8,thick*8)}}
				else
					zapPart.CFrame = CF.N(pos,posie)*CF.N(0,0,MagZ/parts/2)
				end

				lastLightning = zapPart
				Effect{Effect='Fade',Manual=zapPart,Frames=delay}

				thick=thick-inc

			end
		end


		function SoulSteal(whom)
			local torso = (whom:FindFirstChild'Head' or whom:FindFirstChild'Torso' or whom:FindFirstChild'UpperTorso' or whom:FindFirstChild'LowerTorso' or whom:FindFirstChild'HumanoidRootPart')
			if(torso and torso:IsA'BasePart')then
				local Model = Instance.new("Model",Effects)
				Model.Name = whom.Name.."'s Soul"
				whom:BreakJoints()
				local Soul = Part(Model,BrickColor.new'Really red','Glass',V3.N(.5,.5,.5),torso.CFrame,true,false)
				Soul.Name = 'Head'
				NewInstance("Humanoid",Model,{Health=0,MaxHealth=0})
				Effect{
					Effect="Arc",
					Manual = Soul,
					FXSettings={
						Start=torso.CFrame,
						Home = Torso,
						RemoveOnGoal = true,
					}
				}
				local lastPoint = Soul.CFrame.p

				for i = 0, 1, 0.01 do 
					local point = CFrame.new(lastPoint, Soul.Position) * CFrame.Angles(-math.pi/2, 0, 0)
					local mag = (lastPoint - Soul.Position).magnitude
					Effect{
						Effect = "Fade",
						CFrame = point * CF.N(0, mag/2, 0),
						Size = V3.N(.5,mag+.5,.5),
						Color = Soul.BrickColor
					}
					lastPoint = Soul.CFrame.p
					swait()
				end
				for i = 1, 5 do
					Effect{
						Effect="Fade",
						Color = BrickColor.new'Really red',
						MoveDirection = (Torso.CFrame*CFrame.new(M.RNG(-40,40),M.RNG(-40,40),M.RNG(-40,40))).p
					}	
				end
			end
		end

